      const percent = (curScrollTop - fifthPageOffsetY) / speed;
      fifthPageBgcSize.width = "".concat(percent * 50 + 50, "%");
      fifthPageBgcSize.height = "".concat(percent * 50 + 50, "%");
      fifthPageBgcSize.left = "".concat(25 - percent * 25, "%");
      fifthPageBgcSize.top = "".concat(10 - percent * 10, "%");

      if (percent * 50 + 50 <= 110) {
        this.setState({
          fifthPageBgcSize
        });
      }
    }

    if (curScrollTop >= sixthPageOffsetY) {
      const sixthPageContentBoxPos = this.state.sixthPageContentBoxPos;
